import SwiftUI

struct Point {
    let xLongitude: Angle
    let yLatitude: Angle
    let mapProjection: ThematicMapProjection

    init(xLongitude: Angle, yLatitude: Angle) {
        self.xLongitude = xLongitude
        self.yLatitude = yLatitude
        self.mapProjection = .init(xLongitude: xLongitude, yLatitude: yLatitude)
    }
}

/// Recover federal borders from integer truncated, delta-encoded lon/lat data.
let contiguousUSABorderCoordinates: [Point] = zip(xLongitudes, yLatitudes).reduce(into: []) { result, next in
    result.append(.init(
        xLongitude: .init(degrees: 0.01 * next.0 + (result.last?.xLongitude.degrees ?? 0.0)),
        yLatitude: .init(degrees: 0.01 * next.1 + (result.last?.yLatitude.degrees ?? 0.0))
    ))
}

